# class to hold solution for day 6 of advent of code 2022
class TuningTroubles
  # this method takes a string input eg "abcdefghtghrdswojfr"
  def initialize(input)
    @buffer = input.chars
  end

  def find_marker_position(size)
    marker = 0

    @buffer.each_index do |index|
      packet = @buffer.slice(index, size)

      if packet.uniq.size == size
        marker = (index + size) # advent of code starts counting from 1
        break
      end
    end

    marker
  end
end

input = ''
device = TuningTroubles.new(input)
puts device.find_marker_position(14)
